package com.nxt.net.zhifutongqb.base;import android.app.Activity;import android.view.View;import com.nxt.net.zhifutongqb.R;import com.nxt.net.zhifutongqb.widget.ZTitleBar;/** * Created by Maven on 2017/2/28. * Email: cyjiang_11@163.com * Description: */public abstract class  BaseTitleActivity extends BaseActivity {    protected ZTitleBar zTitleBar;    protected View contentview;    /**     * Topbar左侧按钮单击事件     */    public void onLeftClick(View view) {        finish();    }    /**     * Topbar右侧按钮单击事件     */    public void onRightClick(View view) {    }    /**     * 初始化Topbar     *     * @param activity {@link Activity} 使用Topbar的Activity     */    protected void initTopbar(Activity activity, String titlename) {        zTitleBar = (ZTitleBar) activity.findViewById(R.id.ztitlebar);        zTitleBar.setTitle(titlename);        contentview = activity.findViewById(android.R.id.content);    }}