package com.nxt.net.zhifutongqb.bean;import java.io.Serializable;/** * Created by Maven on 2017/3/14. * Email: cyjiang_11@163.com * Description:资讯请求参数 parameterjson */public class ParameterBean implements Serializable {    /**     * paramName : createtime     * paramValue : 2017-01-01     * Operation : GreaterThan     * Logic : AND     *     * 说明：     *① paramName—您要搜索的字段（与返回列表字段一一对应，例如：createtime—发布时间，County—市），     *② paramValue—您要搜索的值，     *③ Operation—操作符     *（     *Equal：相等，【常用】     *NotEqual：不等于，     *Greater：大于，【常用】     * GreaterThan：大于等于，【常用】     *Less：小于，【常用】     *LessThan：小于等于，【常用】     *Null：为空，     *NotNull：不为空，     *Like：包含，【常用】     *NotLike：不包含，     *LeftLike：左包含，     *RightLike：右包含，     *Yesterday：昨天，     *Today：今天，     *Tomorrow：明天，     *LastWeek：上周，     *ThisWeek：本周，     *NextWeek：下周，     *LastMonth：上月，     *ThisMonth：本月，     *NextMonth：下月，     *BeforeDay：今天之前（天），     *AfterDay：今天之后（天）     *），     *④ Logic—匹配符（默认值AND：并且）     */    private String paramName;    private String paramValue;    private String Operation;    private String Logic;    public String getParamName() {        return paramName;    }    public void setParamName(String paramName) {        this.paramName = paramName;    }    public String getParamValue() {        return paramValue;    }    public void setParamValue(String paramValue) {        this.paramValue = paramValue;    }    public String getOperation() {        return Operation;    }    public void setOperation(String Operation) {        this.Operation = Operation;    }    public String getLogic() {        return Logic;    }    public void setLogic(String Logic) {        this.Logic = Logic;    }}