package com.nxt.net.zhifutongqb.bean;import java.io.Serializable;/** * Created by Maven on 2017/3/3. * Email: cyjiang_11@163.com * Description: */public class RequestResponse implements Serializable {    /**     * Success : true     * Code : 1     * Message : 新增成功。     */    private boolean Success;    private String Code;    private String Message;    public boolean isSuccess() {        return Success;    }    public void setSuccess(boolean Success) {        this.Success = Success;    }    public String getCode() {        return Code;    }    public void setCode(String Code) {        this.Code = Code;    }    public String getMessage() {        return Message;    }    public void setMessage(String Message) {        this.Message = Message;    }}