package com.nxt.net.zhifutongqb.utils;import android.app.Activity;import java.util.ArrayList;import java.util.List;/** * Created by Maven on 2017/1/21. * Email: cyjiang_11@163.com * Describe: 活动管理类 */public class ActivityCollectorUtils {    public static List<Activity> activities = new ArrayList<>();    public static void addActivity(Activity activity) {        activities.add(activity);    }    public static void removeActivity(Activity activity) {        activities.remove(activity);    }    public static void finishAll() {        for (Activity activity : activities)            if (!activity.isFinishing()) {                activity.finish();            }    }}