package com.nxt.net.zhifutongqb.utils;import android.content.Context;import android.content.pm.PackageInfo;import android.content.pm.PackageManager;/** * Created by Jan Maven on 2017/5/24. * Email:cyjiang_11@163.com * Description: */public class PackageUtil {    private Context mcontext;    public PackageUtil(Context context) {        this.mcontext = context;    }    public String getVersionName() {        try {            PackageManager manager = mcontext.getPackageManager();            PackageInfo info = manager.getPackageInfo(mcontext.getPackageName(), 0);            String version = info.versionName;            return version;        } catch (Exception e) {            e.printStackTrace();        }        return null;    }    public int getVersionCode() {        try {            PackageManager manager = mcontext.getPackageManager();            PackageInfo info = manager.getPackageInfo(mcontext.getPackageName(), 0);            return info.versionCode;        } catch (Exception e) {            e.printStackTrace();        }        return 0;    }}